#!/usr/bin/perl -w
# $Id: Makefile.PL 2693 2008-01-02 09:54:19Z hospelt $
use 5.006;
use ExtUtils::MakeMaker;
use vars qw($VERSION);
$VERSION = "1.000";

WriteMakefile
    (NAME	=> 'WEC::SMTP',
     VERSION	=> '0.01', # finds $VERSION
     PREREQ_PM	=> {
	 WEC		=> 0.01,
	 "Test::More"	=> 0.11,	# For the tests only
     },
     AUTHOR	=> 'Ton Hospel <WEC::SMTP@ton.iguana.be>',
     $^O eq "MSWin32" ? (
         PM_FILTER	=> '$(PERL) -p -e1',
     ) : (),
     clean		=> {
         FILES => '$(DISTNAME).ppd ppm cover_db',
     },
);

# START MY
# autogenerated by release_pm
BEGIN {
package MY;
our %postamble;

$postamble{ppm} = <<'EOF';
ppm: $(DISTNAME).ppd
EOF

$postamble{ppd} = <<'EOF';
$(DISTNAME).ppd: all ppd
	makeppd.pl "--perl=$(PERL)" --min_version=1.013 "--zip=$(ZIP)" "--tar=$(TAR)" "--compress=$(COMPRESS)" --leave=ppm $(DISTNAME).ppd $(VERSION)
EOF

$postamble{cover} = <<'EOF';
cover:
	cover -delete
	-HARNESS_PERL_SWITCHES=-MDevel::Cover make test
	cover
EOF

$postamble{ppm_install} = <<'EOF';
ppm_install: $(DISTNAME).ppd
	ppm install ppm/$(DISTNAME).ppd
EOF

$postamble{ppm_uninstall} = <<'EOF';
ppm_uninstall:
	ppm uninstall $(DISTNAME)
EOF

sub postamble {
    return shift->SUPER::postamble() . join("
", @postamble{sort {uc $a cmp uc $b || $a cmp $b } keys %postamble});
}
}
# END MY
